public class nonPrimitive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Non-Primitive data types -> String, Arrays and Classes
		
		//String type
		String myNAME = "Harshanth K Prakash";
		String myCompany = "ISS";
		String myPlace = "Mumbai";
		
		System.out.print("Non-Primitive Data Types \n");
		System.out.print("String:");
		System.out.println("\n Name : " + myNAME);
		System.out.println("\n Company : " + myCompany);
		System.out.println("\n Place : " + myPlace);
		
		//Array type
		System.out.print("\n Array :");
		int[] arr = {1,2,3,4,5};
		for(int i=0;i<5;i++)
		{
			System.out.print(arr[i]+ " ");
		}
		



	}

}
