public class DemoBasicDatatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Primitive data types -> byte, short, int, long, float, double, boolean and char
		
		int number = 5;               // Integer (whole number)
		float floatNumber = 5.99f;    // Floating point number
		char character = 'I';         // Character
		double doubleNumber = 3.5457;  //double
		boolean boolVariable = true; 	// Boolean
		short shortNumber = -2345;		// Short
		long longNumber = 10372834;		// Long
		
		System.out.print("Primitive Data Types \n");
		System.out.println("\n Integer displaying: "+number);
		System.out.println("\n Float Number: " + floatNumber );
		System.out.println("\n Character entered: " + character );
		System.out.println("\n Double Number : " + doubleNumber );
		System.out.println("\n Boolean Entered: " + boolVariable );
		System.out.println("\n Short Number: " + shortNumber );
		System.out.println("\n Long Number: " + longNumber );
		

	}

}
